SELECT u.`code`, u.`name`, u.store_url, u.store_pic_url, u.`status`, u.create_user, DATE_FORMAT( u.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, u.update_user, DATE_FORMAT( u.update_time, '%Y-%m-%d %H:%i:%s' ) AS update_time ( SELECT d.`name` FROM usermodule.um_define AS d WHERE d.`code` = u.`status` ) AS status_name FROM usermodule.um_store_info AS u
1=1
and u.`code` = #{code,jdbcType=VARCHAR}
and u.`name` = #{name,jdbcType=VARCHAR}
and u.`status` = #{status,jdbcType=VARCHAR}
