mapper.select:
select

from systemmodule.sm_filed

	table_code =#{tableCode,jdbcType=VARCHAR}
	
		and file_code = #{fileCode,jdbcType=VARCHAR}
	
	
		and file_name = #{fileName,jdbcType=VARCHAR}

select count(1)
from systemmodule.sm_filed

	table_code =#{tableCode,jdbcType=VARCHAR}
	
		and file_code = #{fileCode,jdbcType=VARCHAR}
	
	
		and file_name = #{fileName,jdbcType=VARCHAR}

mapper.update:
alter table ${tableCode}
Add column ${fieldCode}
${fieldType}

	not null


	default ${fieldDefault}
