mapper.select:
select

from systemmodule.sm_table

	1=1
	
		and table_code = #{tableCode,jdbcType=VARCHAR}
	
	
		and table_name = #{tableName,jdbcType=VARCHAR}
	
	
		and type = #{type,jdbcType=VARCHAR}

select count(1)
from systemmodule.sm_table

	1=1
	
		and table_code = #{tableCode,jdbcType=VARCHAR}
	
	
		and table_name = #{tableName,jdbcType=VARCHAR}
	
	
		and type = #{type,jdbcType=VARCHAR}
