mapper.update:
update sm_menu_group
name = #{name,jdbcType=VARCHAR}, update_user = #{updateUser,jdbcType=VARCHAR}, update_time = STR_TO_DATE(#{updateTime,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%s'),
where `code`=#{code,jdbcType=VARCHAR}

mapper.select:
select

from sm_menu_group

	 create_user <>'system' 
	
	and	`name` =#{name,jdbcType=VARCHAR}
	

limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(1) from sm_menu_group
create_user <>'system'
and `name` =#{name,jdbcType=VARCHAR}
