mapper.select:
SELECT smp.`code`, smp.`name`, sm.`name` AS menu_name, (select sd.`name` from sm_define as sd where sd.`code`=smp.permission_type) as permission_name, smp.flag_able, smp.create_user, DATE_FORMAT(smp.create_time,'%Y-%m-%d %H:%i:%s') as create_time, smp.update_user, DATE_FORMAT(smp.update_time,'%Y-%m-%d %H:%i:%s') as update_time, smp.description FROM sm_menu_permission AS smp LEFT JOIN sm_menu AS sm ON smp.menu_code = sm.`code`
sm.is_deleted=0 and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
and smp.name = #{name,jdbcType=VARCHAR}
and smp.flag_able = #{flagAble,jdbcType=INTEGER}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM sm_menu_permission AS smp LEFT JOIN sm_menu AS sm ON smp.menu_code = sm.`code`
sm.is_deleted=0 and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
and smp.name = #{name,jdbcType=VARCHAR}
and smp.flag_able = #{flagAble,jdbcType=INTEGER}
