mapper.select:
SELECT smp.`code`, smp.`name`, sm.`name` AS menu_name, sm.create_user, DATE_FORMAT(sm.create_time,'%Y-%m-%d %H:%i:%s') as create_time, sm.update_user, DATE_FORMAT(sm.update_time,'%Y-%m-%d %H:%i:%s') as update_time, smp.description FROM sm_menu_permission AS smp LEFT JOIN sm_menu AS sm ON smp.menu_code = sm.`code`
sm.is_deleted=0
and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM sm_menu_permission AS smp LEFT JOIN sm_menu AS sm ON smp.menu_code = sm.`code`
sm.is_deleted=0
and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
and smp.menu_code = #{menuCode,jdbcType=VARCHAR}
