mapper.delete:
delete from
systemmodule.sm_user_role

	user_code = #{userCode,jdbcType=VARCHAR}
	AND role_code
	=#{roleCode,jdbcType=VARCHAR}

mapper.select:
SELECT r.`name`, r.en_name, r.`status`, r.create_user, DATE_FORMAT(r.create_time,'%Y-%m-%d %H:%i:%s') as create_time FROM sm_user_role AS ur LEFT JOIN sm_role AS r ON ur.role_code = r.`code`
ur.user_code=#{userCode,jdbcType=VARCHAR}
ORDER BY ur.create_time DESC
